import{_ as a,c as t,o as l,a6 as d}from"./chunks/framework.B6yjUw6z.js";const p=JSON.parse('{"title":"推荐系统与 web 服务的区别及 Lambda 架构","description":"","frontmatter":{},"headers":[],"relativePath":"dev-tools/backend/backend-01.md","filePath":"dev-tools/backend/backend-01.md","lastUpdated":1716110663000}'),e={name:"dev-tools/backend/backend-01.md"},i=d('<h1 id="推荐系统与-web-服务的区别及-lambda-架构" tabindex="-1">推荐系统与 web 服务的区别及 Lambda 架构 <a class="header-anchor" href="#推荐系统与-web-服务的区别及-lambda-架构" aria-label="Permalink to &quot;推荐系统与 web 服务的区别及 Lambda 架构&quot;">​</a></h1><h2 id="一、推荐系统与-web-服务" tabindex="-1">一、推荐系统与 web 服务 <a class="header-anchor" href="#一、推荐系统与-web-服务" aria-label="Permalink to &quot;一、推荐系统与 web 服务&quot;">​</a></h2><table><thead><tr><th>项目</th><th>思维形式</th><th>侧重点</th><th>描述</th></tr></thead><tbody><tr><td>web 项目</td><td>对结果有确定的预期</td><td>侧重于处理复杂逻辑</td><td>web 项目通常用于处理复杂逻辑、处理高并发、实现高可用，构建一个稳定的信息流</td></tr><tr><td>推荐系统</td><td>结果是不确定的、是个概率问题</td><td>侧重于追求指标增长</td><td>推荐系统 追求指标增长如 留存率、阅读时间、GMV 电商网站成交金额、VV 视频网站 (Video View)</td></tr></tbody></table><h2 id="二、大数据-lambda-架构" tabindex="-1">二、大数据 Lambda 架构 <a class="header-anchor" href="#二、大数据-lambda-架构" aria-label="Permalink to &quot;二、大数据 Lambda 架构&quot;">​</a></h2><h3 id="_2-1-lambda-概述" tabindex="-1">2.1 Lambda 概述 <a class="header-anchor" href="#_2-1-lambda-概述" aria-label="Permalink to &quot;2.1 Lambda 概述&quot;">​</a></h3><div class="important custom-block github-alert"><p class="custom-block-title">IMPORTANT</p><p></p><p>大数据 Lambda 系统架构是由 Twitter 工程师 Nathan Marz （Storm 项目发起人提出的） ，该架构旨在 <strong>提供一个结合实时数据和 Hadoop 预先计算的数据环境和混合平台</strong>，提供一个实时的数据试图，其架构包括：</p></div><ol><li>批处理层 <ul><li>数据不可变，可进行任何计算，可水平拓展</li><li>高延迟 几分钟 - 几小时 （计算量和数据量不同）</li><li>日志收集 Flume</li><li>分布式存储 Hadoop</li><li>分布式计算 Hadoop MapReduce、Spark</li><li>视图存储数据库 <ul><li>NoSQL ： Redis、HBase、Cassandra</li><li>MySQL</li><li>Mgondb</li></ul></li></ul></li><li>实时处理层 <ul><li>流式处理、持续计算</li><li>存储和分析某个窗口期内的数据</li><li>最终正确性 （Eventual accuracy）</li><li>实时数据收集 Flume、Kafka</li><li>实时数据分析 Spark、Streaming、Storm、Flink</li></ul></li><li>服务层 <ul><li>支持随机读取</li><li>需要在非常短的时间内返回结果</li><li>读取批处理层和实时处理层结果并对其归并</li></ul></li></ol><h3 id="_2-2-lambda-架构图" tabindex="-1">2.2 Lambda 架构图 <a class="header-anchor" href="#_2-2-lambda-架构图" aria-label="Permalink to &quot;2.2 Lambda 架构图&quot;">​</a></h3><p><img src="https://2024-cbq-1311841992.cos.ap-beijing.myqcloud.com/picgo/202405181204875.png" alt="image-20240518120401808"></p><h3 id="_2-3-lambda-解决的问题及其细节" tabindex="-1">2.3 Lambda 解决的问题及其细节 <a class="header-anchor" href="#_2-3-lambda-解决的问题及其细节" aria-label="Permalink to &quot;2.3 Lambda 解决的问题及其细节&quot;">​</a></h3><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p></p><p>Lambda 架构主要用于解决离线计算和实时计算共同提供服务的问题</p></div><table><thead><tr><th></th><th>离线计算</th><th>实时计算</th></tr></thead><tbody><tr><td>优点</td><td>能够处理的数据量很大 PB 级别</td><td>响应快 ms 级别</td></tr><tr><td>缺点</td><td>速度比较慢 min 级别</td><td>能够处理的数据量小</td></tr><tr><td>涉及的框架</td><td>Hadoop、Spark core、Spark sql、Hive</td><td>Spark streaming、Storm、Flink</td></tr></tbody></table><ol><li>消息中间件 <ul><li>Flume 日志采集系统</li><li>Kafka 消息队列</li></ul></li><li>存储相关 <ul><li>HBase NoSql 数据库</li><li>Hive sql 操作 HDFS 数据</li></ul></li></ol>',13),o=[i];function r(b,s,c,h,n,m){return l(),t("div",null,o)}const _=a(e,[["render",r]]);export{p as __pageData,_ as default};
